import os
from huggingface_hub import snapshot_download, list_models

# ê²€ìƒ‰í•  ëª¨ë¸ ì €ì¥ì†Œ(repository) íŒ¨í„´ ì„¤ì •
# ì‘ì„±ì(author)ëŠ” 'Lots-of-LoRAs'ì´ê³ ,
# ì´ë¦„ì— 'Mistral-7B-Instruct-v0.2-4b-r16-task'ê°€ í¬í•¨ëœ ëª¨ë“  ëª¨ë¸ì„ ê²€ìƒ‰í•©ë‹ˆë‹¤.
author = "Lots-of-LoRAs"
search_pattern = "Mistral-7B-Instruct-v0.2-4b-r16-task"

# ì €ì¥ ìœ„ì¹˜
cache_dir = "/workspace/Weight_compression/Wparam_dataset/lora/snapshots"

print("ğŸ” Hugging Face Hubì—ì„œ ì €ì¥ì†Œ ëª©ë¡ì„ ê²€ìƒ‰ ì¤‘ì…ë‹ˆë‹¤...")
try:
    # APIë¥¼ ì´ìš©í•´ ì¡°ê±´ì— ë§ëŠ” ëª¨ë“  ëª¨ë¸ ì •ë³´ ê°€ì ¸ì˜¤ê¸°
    model_infos = list_models(author=author, search=search_pattern)
    
    # ëª¨ë¸ ì •ë³´ì—ì„œ repo_idë§Œ ì¶”ì¶œ
    repo_ids = [info.id for info in model_infos]
    
    if not repo_ids:
        print("âš ï¸ ê²€ìƒ‰ëœ ëª¨ë¸ì´ ì—†ìŠµë‹ˆë‹¤. íŒ¨í„´ì„ ë‹¤ì‹œ í™•ì¸í•´ì£¼ì„¸ìš”.")
    else:
        print(f"âœ… ì´ {len(repo_ids)}ê°œì˜ ì €ì¥ì†Œë¥¼ ì°¾ì•˜ìŠµë‹ˆë‹¤.")

        # ë‹¤ìš´ë¡œë“œ ë””ë ‰í† ë¦¬ ìƒì„±
        os.makedirs(cache_dir, exist_ok=True)

        # ì°¾ì€ ëª¨ë“  repo_idì— ëŒ€í•´ ë‹¤ìš´ë¡œë“œ ì‹œë„
        for repo_id in repo_ids:
            # huggingface_hubëŠ” repo_idì˜ '/'ë¥¼ '--'ë¡œ ë°”ê¿”ì„œ í´ë”ë¥¼ ìƒì„±í•©ë‹ˆë‹¤.
            # ì˜ˆ: 'user/model' -> 'models--user--model'
            repo_folder_name = "models--" + repo_id.replace("/", "--")
            target_dir = os.path.join(cache_dir, repo_folder_name)

            # ì´ë¯¸ í•´ë‹¹ í´ë”ê°€ ì¡´ì¬í•˜ëŠ”ì§€ í™•ì¸í•˜ì—¬ ë‹¤ìš´ë¡œë“œë¥¼ ê±´ë„ˆëœë‹ˆë‹¤.
            if os.path.exists(target_dir):
                print(f"âœ… Already exists: {repo_id}. Skipping.")
                continue

            # ë‹¤ìš´ë¡œë“œ ì‹œë„
            try:
                print(f"ğŸš€ Downloading {repo_id}...")
                local_dir = snapshot_download(
                    repo_id=repo_id,
                    cache_dir=cache_dir,
                    local_dir_use_symlinks=False,
                    resume_download=True
                )
                print(f"âœ… Downloaded {repo_id} to {local_dir}")
            except Exception as e:
                print(f"âš ï¸ Failed to download {repo_id}: {e}")
        
        print("\nğŸ‰ All downloads finished.")

except Exception as e:
    print(f"ğŸ›‘ API ìš”ì²­ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤: {e}")

